import React from "react";
import "./TopPick.css";
import axios from "axios";
import { useState, useEffect } from "react";

const TopPick = () => {
  const [list, setList] = useState([]);

  useEffect(() => {
    getImage();
  }, []);

  function getImage() {
    axios
      .get(`/api/tweets`)
      .then((response) => {
        setList(response.list.statuses);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  return (
    <>
      <div className="topList">
        <img src={"./image/nba.png"} />
        <img src={"./image/nasa.png"} />
        <button>
          {" "}
          <img onClick={getImage} src={"./image/tesla.png"} />
        </button>
        <img src={"./image/mkbhd.png"} />
        <img src={"./image/ryan.png"} />
      </div>
      <div>
        <ul className="tweetFrame">
          {list.map((person) => (
            <li className="tweetBox" key={person.id}>
              {person.text}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default TopPick;
